	mem_pw = ${memPw}
	, mem_name = #{memName}
	, mem_gender = #{memGender}
	, mem_email = #{memEmail}
	, mem_phone = #{memPhone}
	, mem_postcode = #{memPostcode}
	, mem_address1 = #{memAddress1}
	, mem_address2 = #{memAddress2}

		, mem_profileimg = #{memProfileimg}

where mem_no = #{memNo}
